int maxSubSeqSumn2(int A[], int N){
	int i, j, thisSum, maxSum = 0;
	//i 代表子列左端位置，j 代表子列右端位置
	for(i = 0; i < N; i++){
	//初始化 thisSum 为 0
	//thisSum是从 A[i]到 A[j]的和
	//初始化的目的是当子列左端位置改变时重新开始计算 thisSum
	thisSum = 0;
		for(j = i; j < N; j++){
			thisSum += A[j];
			if(thisSum > maxSum){
				maxSum = thisSum;
			}
		}
	}
	return maxSum;
}


