int DivideAndConquer(int A[], int Left, int Right){
	//声明存储左右最大子列结果的变量
	int MaxLeftSum, MaxRightSum;
	//声明存储跨分割线子列结果的变量
	int MaxLeftBorderSum, MaxRightBorderSum;
	//声明临时存储跨分割线子列和的变量
	int LeftBorderSum, RightBorderSum;
	//其他要声明的变量,考虑for循环的变量i,j,...
	int Center, i;

	//将要求和的子列从中间分割，分别计算左、右、跨分割线的子列和，然后比较得出最大的子列和
	//不断递归将子列拆分为更小的子列，直到子列只剩下一个数，然后再层层合并结果，最终合而治之
	//递归要求有结束条件，如下递归的趋势是左位置越来越接近右位置，右位置不可超越到左位置的左边
	//因此至少是 Right >= Left, 结束条件会是破坏此状态的临界点
	if(Right == Left){
	//如果递归终止条件触发，那么左右位置必定相等，这时如果子列此位置值大于 0 可以任意返回一边，否则返回0
		if(A[Left] > 0) return A[Left];
		else return 0;
	}

	Center = (Left + Right) / 2;
	//函数定义时，编写的参数是形参，需要明确定义传入参数的类型和名字，例如 int List[]
	//但是调用函数时，传入的实参，是一个实际上存在的参数，因此传入数组时可以直接写 List，因为c中数组是指针
	MaxLeftSum = DivideAndConquer(A, Left, Center);
	MaxRightSum = DivideAndConquer(A, Center + 1, Right);

	//接下来计算跨分割线的子列和，参考上一个函数的实现
	//计算左子列分割线的子列和
	LeftBorderSum = 0; MaxLeftBorderSum = 0;
	for(i = Center; i >= Left; i--){
		LeftBorderSum += A[i];
		if(LeftBorderSum > MaxLeftBorderSum)
			MaxLeftBorderSum = LeftBorderSum;
	}

	//计算右子列分割线的子列和
	RightBorderSum = 0; MaxRightBorderSum = 0;
	for(i = Center + 1; i <= Right; i++){
		RightBorderSum += A[i];
		if(RightBorderSum > MaxRightBorderSum)
			MaxRightBorderSum = RightBorderSum;
	}

	//返回治的结果
	return MaxSum(MaxLeftSum, MaxRightSum, MaxLeftBorderSum + MaxRightBorderSum);
}

int MaxSum(int a, int b, int c){
	return a > b ? a > c ? a : c : b > c ? b : c;
}

int maxSubSeqSumlogn(int A[], int N){
	return DivideAndConquer(A, 0, N-1);
}

