//实时处理
int maxSubSeqSumn(int A[], int N){
	int thisSum, maxSum;
	//所有程序都声明一个i,用于潜在的for循环
	int i;
	thisSum = maxSum = 0;
	for(i = 0; i < N; i++){
		thisSum += A[i];
		//如果当前子列和大于最大子列和，则更新最大子列和
		if(thisSum > maxSum)
			maxSum = thisSum;
		//如果当前子列和小于0，则重新开始计算子列和
		//因为如果当前子列和小于0，那么它对后续子列和的贡献必定为负，因此需要重新开始计算
		else if(thisSum < 0)
			thisSum = 0;
	}
	return maxSum;
}

// n² 的算法
int maxSubSeqSumn2(int A[], int N){
	int i, j, thisSum, maxSum;
	//初始化最大子列和为0,c语言中变量未初始化时，值是不确定的，因此需要初始化
	maxSum = 0;
	for(i = 0; i < N; i++){
		//初始化当前子列和为0
		thisSum = 0;
		for(j = i; j < N; j++){
			thisSum += A[j];
			//如果当前子列和大于最大子列和，则更新最大子列和
			if(thisSum > maxSum)
				maxSum = thisSum;
		}
	}
	return maxSum;
}

//This function return a LinkedList type,which is the edited version of the original linkedlist
LinkedList editLinkedList(int x, int k, LinkedList L){
	//Create a pointer to locate the head of current linkedlist
	Node *p = L->next;
	//if p not null which indicates that there are still unvisited nodes in L;
	while(p){
		if(p->data == x) p->data = k;
		p = p->next;
	}
	return L;
}

