dict.string:
dir_path="$(osascript -l JavaScript -e '
  const frontmost_app_name = Application("System Events").applicationProcesses.where({ frontmost: true }).name()[0]

  if (frontmost_app_name === "Finder") {
    decodeURI(Application("Finder").insertionLocation().url()).slice(7).slice(0, -1)
  } else if(frontmost_app_name === "Path Finder") {
    decodeURI(Application("Path Finder").finderWindows[0].target.url()).slice(7).slice(0, -1)
  } else {
    decodeURI(Application("Finder").desktop.url()).slice(7).slice(0, -1)
  }
')"

IFS=$'/\n'

for file in ${@}; do
  file_extension="$([[ "${file}" == *.* ]] && echo ".${file##*.}" || echo '')"
  file_no_extension="${file%.*}"

  [[ -z "${file_extension}" ]] && file_extension=".txt"

  file_location="${dir_path}/${file_no_extension}${file_extension}"

  touch "${file_location}"
  if [[ "${open_files}" == 'yes' ]]; then open "${file_location}"; fi
done
