/* ==UserStyle==
@name           bilibili搜索引擎首页样式
@namespace      hakadao-bilibili-simple-home
@version        1.7.9
@description    b站搜索引擎首页样式风格，支持几种不同的样式效果
@author         Hakadao <a578457889743@gmail.com> (https://www.hakadao.top)
@homepageURL    https://github.com/hakadao/bilibili-simple-home
@supportURL     https://github.com/hakadao/bilibili-simple-home/issues
@updateURL      https://userstyles.world/api/style/3022.user.css
@license        MIT
@preprocessor   uso

@advanced dropdown HOME_STYLE "首页样式" {
	HOME_STYLE_FULL_1 "全屏显示壁纸，不能下滑查看更多视频" <<<EOT
        /*
        **全屏显示壁纸，不能下滑查看更多视频
        *\/

        .bili-banner {
            height: 100vh!important;
        }

        .b-wrap,
        .international-footer {
            display: none;
        } 
		/* 新首页 *\/
		.bili-header .bili-header__banner {
		    height: 100vh!important;
			max-height: 100vh !important;
		}
		.bili-header__channel,
		.bili-layout,
		.palette-button-outer,
		.header-channel,
		.bili-feed4-layout {
		    display: none!important;
		}
	EOT;

    HOME_STYLE_FULL_2 "全屏显示壁纸" <<<EOT
        /*
        **全屏显示壁纸
        *\/

        .bili-banner {
            height: 100vh !important;
        } 
		/* 新首页 *\/
		.bili-header .bili-header__banner {
		    height: 100vh !important;
			max-height: 100vh !important;
		}
    EOT;

	HOME_STYLE_HALF_1 "【样式1】半屏显示壁纸" <<<EOT
        /*
        **【样式1】半屏显示壁纸
        *\/

        .bili-banner {
            height: calc(100vh - 363px)!important;
            min-height: 450px!important;
            z-index: -1!important;
        }

        @media screen and (max-width: 1438px) {
            .first-screen .space-between {
                margin-bottom: .5em
            }
        }

        .primary-menu-itnl {
            margin-top: -50px!important;
            background-color: var(--a-content-1)!important;
            backdrop-filter: var(--a-filter-glass);
        }

        /*搜索框容器*\/

        .international-header .nav-search-box {
            top: 30vh!important;
        }

        @media screen and (max-height: 830px) {
            .international-header .nav-search-box {
                top: 220px!important;
            }
        }

        /*强行制作出搜索栏上的logo*\/

        .international-header .nav-search-box:before {
            top: -300%;
        }

		/* 新首页 *\/
		.bili-header .bili-header__banner {
		  height: calc(100vh - 370px)!important;
		  min-height: 400px;
		  max-height: calc(100vh - 370px) !important;
		}
    EOT;

	HOME_STYLE_HALF_2 "【样式2】半屏显示壁纸" <<<EOT
        /*
        **【样式2】半屏显示壁纸
        *\/

        .bili-banner {
            height: 30vh !important;
			min-height: 210px !important;
            z-index: -1!important;
        }

        .primary-menu-itnl {
            margin-top: -35px!important;
            background-color: var(--a-content-1)!important;
            backdrop-filter: var(--a-filter-glass);
        }

        /*搜索框容器*\/
        .international-header .nav-search-box {
            top: 12vh!important;
        }

		@media screen and (max-height: 830px) {
            .international-header .nav-search-box {
                top: 70px!important;
            }
        }

        /*观看列表按钮位置调整*\/
        .first-screen .space-between {
            margin-top: 1em!important;
            margin-bottom: 0!important;
        }

        /*强行制作出搜索栏上的logo*\/
        .international-header:not(.fixed-top) .nav-search-box:after {
            top: 45%!important;
            left: -200px!important;
            transform: scale(.6);
        } 

		/* 新首页 *\/
		.bili-header .bili-header__banner {
			height: 30vh !important;
			min-height: 210px !important;
		    max-height: 30vh !important;
		}

		/*搜索框容器*\/
        #i_cecream .bili-header :not(.slide-down) .center-search-container .center-search__bar {
			transform: translateY(12vh);
        }

		@media screen and (max-height: 830px) {
            #i_cecream .bili-header :not(.slide-down) .center-search-container .center-search__bar {
                transform: translateY(70px);
            }
        }

		/*logo*\/
        .bili-header .center-search-container::after {
            margin-left: -400px!important;
            transform: scale(.6) translateX(-50%) translateY(-110px) !important;
        } 
        EOT;
}

@advanced dropdown THEME "主题选择" {
THEME_DARK_AUTO "暗色模式（跟随系统）" <<<EOT
		@media screen and (prefers-color-scheme: dark) {
			* {
				color-scheme: dark;
			}
			:root {
				--a-bgcolor: hsl(230 12% 6%);
				--a-mask-banner-color: hsl(230 12% 6% / var(--a-mask-banner-opacity));

				--a-content-1: hsl(230 12% 10%);
				--a-content-1-solid: hsl(230 12% 10%);

				--a-elevated-1: hsl(230 12% 14%);
				--a-elevated-1-solid: hsl(230 12% 14%);
				--a-elevated-2: hsl(230 12% 18%);
				--a-elevated-2-solid: hsl(230 12% 18%);

				--a-divide: rgba(120, 123, 128, 0.3);
				--a-depth-white: contrast(0) invert(1);
				--a-fill-1: rgba(120, 123, 128, .4);
				--a-fill-2: rgba(120, 123, 128, 0.3);

				--a-text-1: hsl(215 19% 98%);
				--a-text-2: hsl(215 19% 95% / 90%);
				--a-text-3: hsl(240 33% 94% / 60%);
			}
			@supports (backdrop-filter: blur(15px)) {
				:root {
					--a-filter-glass: blur(15px) saturate(180%);
					--a-content-1: hsl(230 12% 10% / 60%);
					--a-elevated-1: hsl(230 12% 14% / 60%);
					--a-elevated-2: hsl(230 12% 18% / 60%);
				}
			}
		}
    EOT;

	THEME_DARK "暗色模式" <<<EOT
		* {
			color-scheme: dark;
		}
       :root {
			--a-bgcolor: hsl(230 12% 6%);
			--a-mask-banner-color: hsl(230 12% 6% / var(--a-mask-banner-opacity));

			--a-content-1: hsl(230 12% 10%);
			--a-content-1-solid: hsl(230 12% 10%);

			--a-elevated-1: hsl(230 12% 14%);
			--a-elevated-1-solid: hsl(230 12% 14%);
			--a-elevated-2: hsl(230 12% 18%);
			--a-elevated-2-solid: hsl(230 12% 18%);

			--a-divide: rgba(120, 123, 128, 0.3);
			--a-depth-white: contrast(0) invert(1);
			--a-fill-1: rgba(120, 123, 128, .4);
			--a-fill-2: rgba(120, 123, 128, 0.3);

			--a-text-1: hsl(215 19% 98%);
			--a-text-2: hsl(215 19% 95% / 90%);
			--a-text-3: hsl(240 33% 94% / 60%);
		}
		@supports (backdrop-filter: blur(15px)) {
			:root {
				--a-filter-glass: blur(15px) saturate(180%);
				--a-content-1: hsl(230 12% 10% / 60%);
				--a-elevated-1: hsl(230 12% 14% / 60%);
				--a-elevated-2: hsl(230 12% 18% / 60%);
			}
		}
    EOT;

    THEME_BLACK_AUTO "黑色模式（跟随系统）" <<<EOT
		@media screen and (prefers-color-scheme: dark) {
			* {
				color-scheme: dark;
			}
			:root {
				--a-bgcolor: hsl(0 0% 0%);
				--a-mask-banner-color: hsl(0 0% 0% / var(--a-mask-banner-opacity));

				--a-content-1: hsl(230 12% 8%);
				--a-content-1-solid: hsl(230 12% 8%);

				--a-elevated-1: hsl(230 12% 14%);
				--a-elevated-1-solid: hsl(230 12% 14%);
				--a-elevated-2: hsl(230 12% 20%);
				--a-elevated-2-solid: hsl(230 12% 20%);

				--a-divide: rgba(84, 84, 88, 0.65);
				--a-depth-white: contrast(0) invert(1);
				--a-fill-1: rgba(120, 123, 128, .4);
				--a-fill-2: rgba(120, 123, 128, 0.3);

				--a-text-1: hsl(215 19% 98%);
				--a-text-2: hsl(215 19% 95% / 90%);
				--a-text-3: hsl(240 33% 94% / 60%);
			}
			@supports (backdrop-filter: blur(15px)) {
				:root {
					--a-filter-glass: blur(15px) saturate(180%);
					--a-content-1: hsl(230 12% 8% / 60%);
					--a-elevated-1: hsl(230 12% 14% / 60%);
					--a-elevated-2: hsl(230 12% 20% / 60%);
				}
			}
		}
    EOT;

	THEME_BLACK "黑色模式" <<<EOT
		* {
			color-scheme: dark;
		}
		:root {
			--a-bgcolor: hsl(0 0% 0%);
			--a-mask-banner-color: hsl(0 0% 0% / var(--a-mask-banner-opacity));

			--a-content-1: hsl(230 12% 8%);
			--a-content-1-solid: hsl(230 12% 8%);

			--a-elevated-1: hsl(230 12% 14%);
			--a-elevated-1-solid: hsl(230 12% 14%);
			--a-elevated-2: hsl(230 12% 20%);
			--a-elevated-2-solid: hsl(230 12% 20%);

			--a-divide: rgba(84, 84, 88, 0.65);
			--a-depth-white: contrast(0) invert(1);
			--a-fill-1: rgba(120, 123, 128, .4);
			--a-fill-2: rgba(120, 123, 128, 0.3);

			--a-text-1: hsl(215 19% 98%);
			--a-text-2: hsl(215 19% 95% / 90%);
			--a-text-3: hsl(240 33% 94% / 60%);
		}
		@supports (backdrop-filter: blur(15px)) {
			:root {
				--a-filter-glass: blur(15px) saturate(180%);
				--a-content-1: hsl(230 12% 8% / 60%);
				--a-elevated-1: hsl(230 12% 14% / 60%);
				--a-elevated-2: hsl(230 12% 20% / 60%);
			}
		}
    EOT;

	THEME_LIGHT "亮色模式" <<<EOT
    EOT;
}

@advanced dropdown SCROLL_BAR_STYLE "滚动条样式调整" {
	SCROLL_BAR_THEME "配合样式" <<<EOT
		/* custom chrome, edge scrollbar *\/
		::-webkit-scrollbar {
			width: 8px;
			height: 8px;
		}
		::-webkit-scrollbar-corner,
		::-webkit-scrollbar-track {
			background: transparent;
		}
		html > ::-webkit-scrollbar-track {
			background: var(--a-bgcolor);
		}
		::-webkit-scrollbar-thumb {
			background-color: hsl(0 0% 50% / .6);
			border-radius: 20px;
		}
		/* custom firefox scrollbar *\/
		html > * {
			scrollbar-color: hsl(0 0% 50% / .6) var(--a-bgcolor);
			scrollbar-width: thin !important;
		}
		* {
			scrollbar-color: hsl(0 0% 50% / .6) transparent;
			scrollbar-width: thin !important;
		}
    EOT;

	SCROLL_BAR_NOMAL "不做改动（对 mac 用户支援会很好）" <<<EOT
    EOT;

	SCROLL_BAR_HIDDEN "隐藏滚动条" <<<EOT
		/* custom chrome, edge scrollbar *\/
		::-webkit-scrollbar {
			width: 0;
			height: 0;
		}
		::-webkit-scrollbar-corner,
		::-webkit-scrollbar-track {
			background: transparent;
		}
		/* custom firefox scrollbar *\/
		* {
			scrollbar-width: none;
		}

    EOT;
}

@advanced dropdown LOGO_COLOR "选择 LOGO 图标样式" {
	LOGO_COLOR_THEME "跟随主题颜色" <<<EOT
        var(--a-theme-color)
    EOT;

	LOGO_COLOR_WHITE "白色" <<<EOT
		#FFF
    EOT;
}

@advanced dropdown LOGO_DISPLAY "是否显示 LOGO" {
	LOGO_ON "显示" <<<EOT

    EOT;

	LOGO_OFF "关闭" <<<EOT
		.international-header .nav-search-box:after,
		.bili-header .center-search-container::after {
			display: none
		}
    EOT;
}

@advanced dropdown TOPBAR_LOGO_COLOR "选择顶栏 LOGO 图标颜色" {
	LOGO_COLOR_WHITE "白色" <<<EOT
	/*logo*\/
	.bili-header .left-entry__title::before {
		color: #FFF;
	}
	/*logo右侧箭头*\/
	.bili-header .left-entry__title .mini-header__title .mini-header__arrow {
		color: white;
		filter: drop-shadow(0 0 4px rgba(0, 0, 0, .3));
	}

	.bili-header .left-entry__title {
		filter: drop-shadow(0 2px 4px rgba(0, 0, 0, .3));
	}
    EOT;	

	LOGO_COLOR_THEME "跟随主题颜色" <<<EOT
	/*logo*\/
	.bili-header .left-entry__title::before {
		color: var(--a-theme-color);
	}

	/*logo右侧箭头*\/
	.bili-header .left-entry__title .mini-header__title .mini-header__arrow {
		color: white;
		filter: drop-shadow(0 0 4px var(--a-theme-color));
	}

	.bili-header .left-entry__title {
		filter: drop-shadow(0 0 4px var(--a-theme-color-50));
	}
    EOT;
}

@advanced dropdown LOGO_GLOW_EFFECT "LOGO 发光效果" {
	LOGO_GLOW_EFFECT_ON "启用" <<<EOT
	.bili-header .center-search-container::after {
		text-shadow: 0 0px 16px var(--a-theme-color-50) !important;
	}
    EOT;

	LOGO_GLOW_EFFECT_OFF "禁用" <<<EOT
    EOT;
}

@advanced dropdown LOGO_DISPLAY "是否显示 LOGO" {
	LOGO_ON "显示" <<<EOT

    EOT;

	LOGO_OFF "关闭" <<<EOT
		.international-header .nav-search-box:after,
		.bili-header .center-search-container::after {
			display: none
		}
    EOT;
}

@advanced color THEME_COLOR_NEW "选择主题色（仅支援十六进制非透明颜色，如默认蓝 #00a1d6、默认粉 #fb7299）" #00a1d6

@advanced dropdown DIV_BG "选择背景" {
	BG_NONE "无背景"  <<<EOT "" EOT;
	BG_2 "unsplash随机" <<<EOT url(https://source.unsplash.com/1920x1080/?nature) EOT;
	BG_3 "BML2019 VR（pid: 74271400）" <<<EOT url(https://pic.imgdb.cn/item/638e1d63b1fccdcd36103811.jpg) EOT;
	BG_4 "2020拜年祭活动 " <<<EOT url(https://pic.imgdb.cn/item/638e1d7ab1fccdcd36106346.jpg) EOT;
	BG_5 "2020BDF" <<<EOT url(https://pic.imgdb.cn/item/63830f1816f2c2beb1868554.jpg) EOT;
  BG_DIV "自定义" <<<EOT var(--a-bg-custom) EOT;
}

@advanced text CUSTOM_BG "自定义背景" "url()"

@advanced text MASK_OPACITY "设置背景遮罩透明度（0为完全透明，1为完全覆盖背景，允许小数）" "0"

@advanced dropdown BG_ATTACHMENT "背景依附方式" {
	BG_SCROLL "背景随滚轮滚动" <<<EOT
	EOT;

	BG_FIXED "背景固定" <<<EOT
		.bili-header .bili-header__banner {
			background-attachment: fixed !important;
		}
	EOT;
}

@advanced dropdown TOPLINK_THEME "顶栏设计效果" {
	TOPLINK_1 "效果1（通透模式）" <<<EOT
        /*banner上方的链接*\/

        .nav-link .nav-link-ul .nav-link-item .link,
        .nav-user-center .user-con .item .name {
            color: var(--a-text-1);
            text-shadow: none!important;
        }

        /*banner上方的链接后面黑色过渡效果*\/

        .bili-banner .taper-line {
            background: none!important;
        } 
        .bili-header .right-entry__outside .right-entry-text {
            color: var(--a-text-1);
        }
    EOT;

    TOPLINK_2 "效果2（胶囊模式）" <<<EOT
		/*新版首页适配*\/
		.bili-header :not(.slide-down) .right-entry {
			backdrop-filter: var(--a-filter-glass);
			border-radius: 100px;
			box-shadow: var(--a-depth-1);
		}
		bili-header .bili-header__bar .right-entry__outside .right-entry-text {
			text-shadow: 0 0 3px rgba(0, 0, 0, .3);
		}
		.bili-header .right-entry__outside svg,
        .bili-header .bili-header__bar .right-entry__outside .right-entry-text,
		.bili-header .right-entry .right-entry-item .right-entry-text {
			filter: drop-shadow(0 0 3px rgba(0, 0, 0, .3));
		}
    EOT;
}

@advanced dropdown CAPSULE_DESIGN_UP "顶栏右侧文字颜色设计（滚动前）" {
    CAPSULE_DESIGN_1 "默认设计" <<<EOT
