	.bili-header .bili-header__bar .right-entry__outside .right-entry-text {
		color: var(--a-text-1);
	}
    EOT;

    CAPSULE_DESIGN_2 "跟随主题色" <<<EOT
	.bili-header .bili-header__bar .right-entry__outside .right-entry-icon,
	.bili-header .bili-header__bar .right-entry__outside .right-entry-text {
