	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		color: var(--a-text-1)!important;
		filter: none;
	}
    EOT;

    CAPSULE_DESIGN_2 "跟随主题色" <<<EOT
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-icon,
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		color: var(--a-theme-color) !important;
		filter: drop-shadow(0 0 8px var(--a-theme-color)) !important;
	}
    EOT;

    CAPSULE_DESIGN_3 "白色" <<<EOT
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-icon,
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		color: #FFF !important;
		filter: none;
	}
    EOT;

    CAPSULE_DESIGN_4 "黑色" <<<EOT
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-icon,
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		color: #000 !important;
