	}
    EOT;
}

@advanced dropdown SEARCHBAR_DESIGN "搜索框样式（针对自定义背景改善）" {
    SEARCHBAR_DESIGN_1 "默认设计" <<<EOT
    EOT;

    SEARCHBAR_DESIGN_2 "线条设计" <<<EOT
        #internationalHeader.international-header:not(.fixed-top) .nav-search #nav_searchform,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar #nav-searchform{
            backdrop-filter: none!important;
            background: unset!important;
            border: none!important;
            border-bottom: 2px solid white!important;
            padding: 6px 0 5px;
            border-radius: 0!important;
            box-shadow: none!important;
            margin-bottom: .5em;
			filter: drop-shadow(0 2px 4px rgb(0 0 0 / 40%));
        }
        .bili-header :not(.slide-down) .center-search-container .center-search__bar .nav-search-content
        {
            padding: unset!important;
        }
        .bili-header :not(.slide-down) .center-search-container .center-search__bar #nav-searchform{
            padding-left: unset!important;
        }
        #internationalHeader.international-header:not(.fixed-top) .nav-search .nav-search-keyword,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar #nav-searchform .nav-search-input{
            color: white!important;
			filter: drop-shadow(0 2px 4px rgb(0 0 0 / 30%));
        }
        .international-header:not(.fixed-top) .bili-icon_dingdao_sousuo::before,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar .nav-search-btn{
            color: white!important;
        }

		/*33娘处于线条设计搜索条位置调整（新版首页）*\/
		.bili-header :not(.slide-down) .center-search-container .center-search__bar::after {
			right: -40px !important;
			top: -62px !important;
		}
		.bili-header :not(.slide-down) .center-search-container .center-search__bar:focus-within::after {
			transform: translateX(-10px) !important;
			z-index: 10;
			pointer-events: none;
		}
    EOT;
}

@advanced dropdown FOCUS_EFFECTS "搜索条聚焦后暗化效果" {
	FOCUS_EFFECTS_OFF "关闭" <<<EOT
    EOT;

	FOCUS_EFFECTS_ON "开启" <<<EOT
        .international-header .nav-search::before,
        .bili-header .bili-header__bar::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: rgba(0, 0, 0, 0);
            transition: .3s ease-in-out;
            visibility: hidden;
            z-index: -1;
            pointer-events: none;
        }

        .bili-header .bili-header__bar.slide-down::before {
            background: transparent !important;
            backdrop-filter: unset !important;
        }

        .international-header .nav-search:focus-within::before,
        .bili-header  .bili-header__bar:focus-within::before{
            background-color: rgba(0, 0, 0, .4);
            visibility: visible;
            /*[[FOCUS_BLUR]]*\/
        }

        /*取消焦点时搜索记录隐藏*\/
        .international-header .nav-search .bilibili-search-suggest,
        .international-header .nav-search .bilibili-search-history{
            display: none;
        }

        .international-header .nav-search .bilibili-search-suggest:active,
        .international-header .nav-search .bilibili-search-history:active,
        .international-header .nav-search:focus-within .bilibili-search-suggest,
        .international-header .nav-search:focus-within .bilibili-search-history{
            display: block;
        } 
    EOT;
}

@advanced dropdown FOCUS_BLUR "搜索条聚焦后毛玻璃效果（仅在上个效果开启才能实现，有点吃效能）" {
	FOCUS_BLUR_OFF "关闭" <<<EOT
    EOT;

	FOCUS_BLUR_ON "开启" <<<EOT
        backdrop-filter:  blur(8px) 
    EOT;
}

@advanced dropdown SHOW_33 "搜索条聚焦后显示33娘" {
	SHOW_33_ON "开启" <<<EOT
        .international-header .nav-search::after,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar::after{
            content: '';
            position: absolute;
            top: -60px;
            right: -10px;
            width: 100px;
            height: 100px;
            background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/searchBar_33.png);
            background-size: cover;
            z-index: -1;
            transition: .3s ease-out;
            opacity: 0;
        }

        .bili-header :not(.slide-down) .center-search-container .center-search__bar::after {
            background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/searchBar_33_2.png);
            background-size: cover;
        }

        .international-header .nav-search:focus-within::after,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar:focus-within::after{
            transform: translatey(-20px);
            opacity: 1;
        } 

        .bili-header :not(.slide-down) #nav-searchform {
            overflow: unset!important;
        }
    EOT;

	SHOW_33_OFF "关闭" <<<EOT
    EOT;
}

@advanced dropdown SHOW_22 "分区弹出层显示22娘" {
	SHOW_22_ON "开启" <<<EOT
        /*新版分区弹出层显示22娘*\/
		@keyframes slideIn22 {
			from {
				transform: translateX(100%);
				opacity: 0;
			}
		}

		.n-drawer-mask::after {
			content: "";
			position: absolute;
			right: 200px;
			top: 30px;
			background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/drawer_mask_22.png) no-repeat;
			background-size: contain;
			width: 423px;
			height: 100%;
			pointer-events: none;
			animation: 1s slideIn22;
		}
    EOT;

	SHOW_22_OFF "关闭" <<<EOT
    EOT;
}

@advanced dropdown TRENDIND "右侧排行榜" {
	TRENDIND_OFF "禁用" <<<EOT
	/*移除排行榜*\/
	.bili-grid {
		grid-template-columns: repeat(4, 1fr);
	}
	@media (max-width: 1099.9px) {
		.bili-grid {
			grid-template-columns: repeat(3, 1fr);
		}
	}
	.bili-grid aside {
		display: none;
	}
	EOT;

	TRENDIND_ON "开启" <<<EOT
	EOT;
}

@advanced dropdown VIDEO_ZOOM "滑动视频放大效果（如果你电脑性能好的话，启用吧 = =）" {
	VIDEO_ZOOM_OFF "禁用" <<<EOT
	EOT;

	VIDEO_ZOOM_ON "开启" <<<EOT
        /*鼠标滑过视频预览放大效果*\/
        [class*="-card"] [class$="-wrap"] {
            z-index: 0;
        }
        [class*="-card"] [class$="-wrap"]:hover {
            /*更改 z-index 令滑过视频放大了之后不受旁边视频遮挡*\/
            z-index: 10;
        }
        [class$="-card"] [class*="-card__image"],
        [class$="video-card"] [class*="-card__image"]:hover [class*="-card__mask"] {
            transition: all .3s;
        }
        [class$="video-card"] [class*="-card__image"]:hover,
        [class$="video-card"] [class*="-card__image"]:hover [class*="-card__mask"] {
            transform: scale(1.1) translate3d(0, 0, 0);
            z-index: 999999;
            box-shadow: var(--a-depth-2);
        }
        [class$="video-card"] a + .bili-video-card__info {
            transition: .4s;
            position: relative;
            transform: translate3d(0, 0, 0);
        }
        [class$="video-card"] a + .bili-video-card__info::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: -1;
            background: transparent;
            border-radius: var(--a-radius);
            transition: .4s;
            pointer-events: none;
        }
        [class$="video-card"] a:hover + .bili-video-card__info::after {
            top: -2rem;
            left: -1rem;
            width: calc(100% + 2rem);
            height: calc(100% + 3rem);
        }
        [class$="video-card"] a:hover + .bili-video-card__info {
            transform: translate(0, 30%);
        }
        [class$="video-card"] a:hover + .bili-video-card__info::after {
            background: var(--a-elevated-1-solid);
            box-shadow: var(--a-depth-2);
        }
	EOT;
}

