@-moz-document regexp("^https?://www\\.bilibili\\.com/?((index\\.html)|(\\?.*))?") {
	/**
	 *新版首页支援
	 */
	/*  	body {
 		background: var(--a-bgimg) center/cover var(--a-bgcolor) fixed !important;
		will-change: all;

 	} */

	html,
	html body,
	html:not(.iframe),
	html:not(.iframe) body {
		background-color: var(--a-bgcolor)!important
	}


	/*去除礙眼物*/
	/*顶栏左侧多余项目，保留首页按钮*/
	.bili-header .left-entry li:not(:nth-child(1)),
	.bili-header .bili-header__bar:not(.slide-down) .left-entry li:nth-child(1),
	/*大会员*/
	.bili-header .right-entry li:nth-child(2),
	/*热门搜寻*/
	.search-panel .trending,
	/*推广*/
	.eva-extension-area,
	/*广告*/
	.eva-banner,
	.bili-footer,
	/*顶栏动态图banner*/
	.bili-header .bili-header__banner .banner-img,
	/*新版首页无关推介*/
	.feed2-recommend,
	.feed-recommend2,
	/*推介换一换按钮*/
	/* 	.feed2 .feed-roll-btn, */
	/*新版首页分区*/
	.channel-floor,
	/*未登入时右侧推广卡片*/
	.floor-single-card,
	/*屏蔽广告后顶栏之提示*/
	.adblock-tips {
		display: none !important;
	}

	/*推广视频卡片*/
	.business-card {
		/*为什么不直接display none? 因为直接隐藏会导致下拉推广视频无法刷出来*/
		height: 0;
		overflow: hidden
	}

	/*顶栏*/
	.bili-header {
		color: var(--a-text-1);
		transition: .3s;
	}
	/*顶栏未读信息、未读动态*/
	.bili-header [class^="red-num--"],
	/*顶栏弹窗，未读信息*/
	.bili-header .message-entry-popover [class$="--num"] {
		background-color: var(--a-theme-color)
	}
	/*顶栏条*/
	.bili-header .bili-header__bar {
		margin-top: 8px;
		/*修正全屏显示壁纸时元素宽度问题导致出现横向滚动条问题 #28，包括其他几个首页样式的横向滚动条问题*/
		width: calc(100vw - 8px);
		/*修正banner点击顶栏消失问题*/
		display: flex !important;
	}
	/*内测顶栏*/
	.bili-feed4 .bili-header .slide-down {
		box-shadow: none !important;
	}
	.bili-header .left-entry {
		margin-left: 1rem;
	}
	.bili-header .left-entry__title {
		border-radius: 100px;
	}
	/*顶栏logo*/
	.bili-header .left-entry__title::before {
		content: "\e6b4";
		font-family: 'Bilibili Icon';
		font-style: normal;
		font-weight: normal;
		line-height: 0;
		font-size: 30px;
		margin-right: 12px;
	}
	/*去除舊頂欄左側文字首頁，logo*/
	.bili-header .left-entry__title .mini-header__title > span,
	.bili-header .left-entry__title .mini-header__logo {
		display: none;
	}
	@keyframes headerSlideDown {
		from {
			opacity: 0;
		}
		to {
			opacity: 1;
		}
	}
	/*顶栏处于下拉状态时*/
	.bili-header .bili-header__bar.slide-down {
		background: unset;
		box-shadow: none;
		animation: .5s headerSlideDown;
		position: fixed;
	}
	.bili-header .bili-header__bar::after {
		content: "";
		position: fixed;
		top: 0;
		left: 0;
		width: 100%;
		height: 150px;
		background: linear-gradient(rgba(0, 0, 0, .2), transparent);
		z-index: -1;
		pointer-events: none;
		opacity: 0;
		transition: .3s;
	}
	/*当顶栏滑下时，制造出渐变遮罩效果*/
	.bili-header .bili-header__bar.slide-down::after {
		opacity: 1;
	}
	/*顶栏右侧内容 帐号 消息 动态 收藏...*/
	.bili-header .slide-down .right-entry {
		/*这里把它们做成胶囊效果*/
		backdrop-filter: var(--a-filter-glass);
		border-radius: 100px;
		box-shadow: var(--a-depth-2);
		background: var(--a-elevated-2);
		padding: 6px 0;
	}
	.bili-header .slide-down .left-entry .mini-header__title,
	.bili-header .slide-down .left-entry .entry-title,
	.bili-header .slide-down .left-entry .default-entry,
	.bili-header .slide-down .left-entry .loc-mc-box__text,
	.bili-header .slide-down .left-entry .download-entry,
	.bili-header .slide-down .left-entry .loc-entry {
		color: var(--a-text-1);
	}
	/*顶栏登录*/
	.bili-header .header-login-entry {
		color: var(--a-theme-color);
		background-color: var(--a-content-1-solid);
	}
	/*顶栏右侧图标项目*/
	.bili-header .right-entry__outside {
		height: 36px;
		display: flex;
		justify-content: center;
	}
	.bili-header .right-entry__outside .right-entry-icon  {
		transition: color 1s, filter 1s, transform .3s, opacity .3s;
	}
	/*投稿图标不变色*/
	.bili-header .slide-down [class$="entry"] span:not(:nth-last-child),
	.bili-header .slide-down [class$="entry"] svg {
		color: var(--a-text-1)!important;
	}
	.bili-header .right-entry {
		display: flex;
		justify-content: flex-end;
		padding: 8px 0;
		padding-left: 4px !important;
	}
	.bili-header :not(.slide-down) .right-entry {
		padding: 8px 0;
	}
	/*动态提示点*/
	.red-point--dynamic {
		background: var(--a-theme-color);
	}
	/*去除顶栏头像边框*/
	.bili-header .header-entry-mini .v-img > img,
	.bili-header .header-avatar-wrap .header-avatar-wrap--container .bili-avatar {
		border: none;
	}
	/*顶栏右侧面板*/
	.bili-header .bili-header__bar .right-entry__outside .right-entry-text {
		/*这里做的是鼠标滑过图标显示文字动画*/
		position: absolute;
		opacity: 0;
		transition: .3s ease-out;
		transform: translateY(100%);
	}
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		/*当顶部链接效果是效果二时，右侧面板的图标和文字都有 filter: drop-shadow 的效果。slide-down 时要去掉阴影效果*/
		filter: unset;
		/*color: var(--a-text-1)!important;*/
	}
	.bili-header .right-entry__outside:hover .right-entry-text {
		opacity: 1;
		transform: translateY(0);
	}
	/*顶栏右侧面板的图标*/
	.bili-header .right-entry__outside svg {
		transition: .3s ease-out;
		animation: none!important;
	}
	.bili-header .slide-down .right-entry__outside svg {
		filter: unset;
	}
	/*滑过图标显示触发图标上移显示文字动画*/
	.bili-header .right-entry__outside:hover svg {
		transform: translateY(-100%);
		opacity: 0;
	}
	/*投稿禁用上述动画*/
	.bili-header .right-entry__outside:hover .header-upload-entry svg {
		transform: unset;
		opacity: unset;
	}
	/*小于 1279.9px 时禁用滑过图标上移动画，这个像素控制原本是不显示图标下方文字*/
	@media (max-width: 1279.9px) {
		.bili-header .right-entry__outside:hover svg {
			transform: unset;
			opacity: unset;
		}
	}
	/*投稿图标*/
	.bili-header .header-upload-entry {
		background: var(--a-theme-color);
		box-shadow: 0 0 10px var(--a-theme-color-50);
		color: white;
		border-radius: 50px;
		transition: .3s ease-out;
		margin-right: 12px;
	}
	.bili-header .header-upload-entry__icon {
		color: white;
	}
	.bili-header .header-upload-entry:hover {
		background: var(--a-theme-color);
		filter: brightness(1.1)
	}
	/*顶栏遮罩*/
	.bili-header .bili-header__banner::before {
