		background: transparent;
	}
	/*banner 头图背景*/
	#i_cecream,
	.large-header {
		background: unset;
	}
	/*头图*/
	.bili-header .bili-header__banner {
		height: 100vh;
		max-height: unset;
		background: var(--a-bgimg) center/cover !important;
		background-color: var(--a-bgcolor);
		transition: background 1s;
		z-index: 100;
	}
	/*去除 banner 跳转连结*/
	.bili-header .bili-header__banner .header-banner__inner {
		display: none;
	}
	.bili-header .bili-header__banner .head-title {
		display: none;
	}
	.bili-header .bili-header__bar.slide-down + .bili-header__banner::after {
		background-color: var(--a-bgcolor);
		filter: opacity(.6);
	}
	/*头图跳转*/
	.bili-header .bili-header__banner .banner-link {
		display: none;
	}
	.bili-header .bili-header__banner .logo-img {
		width: 210px!important;
		height: 100px!important;
		background: var(--logoimg, url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/logo_white.png)) no-repeat !important;
		background-size: 200px !important;
		opacity: 0;
	}
	.bili-header .center-search-container::after {
