@-moz-document regexp("^https?://www\\.bilibili\\.com/?((index\\.html)|(\\?.*))?") {
	/**
	  * 新首页弹窗调整
	  */
	.v-popover-content {
		border-radius: var(--a-radius)!important;
		border: unset;
		box-shadow: var(--a-depth-2);
		background: var(--a-elevated-2-solid);
	}
	.v-popover-content > * {
		color: var(--a-text-1);
	}
	/*顶栏 logo 的弹出窗，分区弹窗*/
	.left-entry .v-popover-content {
		background: unset;
		box-shadow: var(--a-depth-2);
		border: none;
	}
	/*顶栏滑过 logo 的弹出窗，分区选择*/
	.bili-header .bili-header-channel-panel {
		background: var(--a-elevated-2);
		border-radius: var(--a-radius);
		backdrop-filter: var(--a-filter-glass);
	}
	.bili-header .bili-header-channel-panel .channel-panel__column .channel-panel__item {
		color: var(--a-text-1);
		border-radius: var(--a-radius)
	}
	/*频道分割线*/
	.bili-header .bili-header-channel-panel .channel-panel__column:not(:last-child) {
		border-color: var(--a-divide);
	}
	.bili-header .bili-header-channel-panel .channel-panel__column:last-child {
		border-color: transparent;
	}
	.bili-header .bili-header-channel-panel .channel-panel__column .channel-panel__item:hover {
		background: var(--a-fill-1);
	}
	/*首页分区 更多弹窗面板*/
	.channel-entry-popover,
	.more-channel-popover {
		background: var(--a-elevated-2-solid);
		color: var(--a-text-1);
		border-radius: var(--a-radius);
	}
	/*首页分区 更多弹窗中的子分区*/
	.channel-entry-popover .name,
	.more-channel-popover .name {
		border-radius: var(--a-radius);
	}
	.more-channel-popover .name:hover {
		background: var(--a-fill-1);
	}
	/*排行榜视频信息卡片*/
	.bili-rank-list-video .rank-video-card__popover,
	.bili-rank-list-ogv .rank-ogv-card__popover {
		background-color: var(--a-elevated-2-solid);
		box-shadow: var(--a-depth-2);
		color: var(--a-text-1);
	}
	.bili-rank-list-ogv .rank-ogv-card__popover--tit {
		color: var(--a-text-1)!important
	}
	.bili-rank-list-video .rank-video-card__popover {
		box-shadow: unset;
		border: unset;
	}
	.bili-rank-list-video .rank-video-card__popover--stats {
		border-color: var(--a-divide);
		color: var(--a-text-3);
	}
}
@-moz-document regexp("^https?://www\\.bilibili\\.com/?((index\\.html)|(\\?.*))?") {
	/**
	 * 新版首页顶栏弹窗
	 */
	.bili-header .avatar-panel-popover,
	.bili-header .message-entry-popover .message-inner-list {
		background: var(--a-elevated-1-solid);
		color: var(--a-text-1);
	}
	/*顶栏未登录弹窗图标*/
	.login-tip-content.login-tip-content .login-tip-content-item>svg {
		fill: var(--a-theme-color);
	}
	/*顶栏未登录弹窗 点我注册*/
	.bili-header .login-panel-popover .register-exper {
		color: var(--a-theme-color);
	}
	/*顶栏未登录弹窗 立即登录按钮*/
	.bili-header .login-panel-popover .login-btn:hover:before {
		background: var(--a-theme-color);
	}
	/*顶栏动态*/
	.dynamic-article .split-line .history-tip,
	.dynamic-video .history-tip {
		background: var(--a-elevated-2-solid)!important;
	}
	.favorite-panel-popover__nav .tab-item--active {
		color: white !important;
	}
	.bili-header .avatar-panel-popover .coins-item .coin-item__num,
	.bili-header .avatar-panel-popover .counts-item .single-count-item .count-num,
	.bili-header .message-entry-popover .message-inner-list__item,
	.bili-header .list-item .content[data-v-7a86fe06],
	.bili-header .list-item .name-line[data-v-7a86fe06],
	.wnd_bottom .r-l,
	.bili-header .header-fav-card__info--title,
	.favorite-panel-popover__nav .tab-item,
	.favorite-panel-popover__content .content-bottom__btn {
		color: var(--a-text-1);
	}
	.bili-header .avatar-panel-popover .logout-item,
	.bili-header .avatar-panel-popover .links-item .single-link-item {
		color: var(--a-text-2);
	}
	.bili-header .avatar-panel-popover .coins-item .coin-item__text,
	.dynamic-video .tip-box,
	.bili-header .header-fav-card__info--name {
		color: var(--a-text-3);
	}
	.bili-header .avatar-panel-popover .logout-item .link-icon svg *,
	.bili-header .avatar-panel-popover .logout-item .link-icon *,
	.bili-header .avatar-panel-popover .links-item .single-link-item .link-title svg * {
		fill: var(--a-text-2);
	}
	.bili-header .avatar-panel-popover .split-line {
		background: var(--a-divide);
	}
	.wnd_bottom a {
		background: var(--a-fill-1) !important;
	}
	.bili-header .avatar-panel-popover .counts-item .single-count-item:hover .count-text,
	.bili-header .avatar-panel-popover .counts-item .single-count-item:hover .count-num,
	.favorite-panel-popover__content .content-bottom__btn--play {
		color: var(--a-theme-color);
	}
	.favorite-panel-popover__nav .tab-item--active {
		background: var(--a-theme-color);
	}
	.favorite-panel-popover__content .content-bottom__btn .play-icon * {
		fill: var(--a-theme-color);
	}
	.split-line:before {
		border-color: var(--a-divide)!important;
	}
	.header-tabs-panel,
	.favorite-panel-popover__nav,
	.favorite-panel-popover__content .content-bottom__btn--view,
	.favorite-panel-popover__content .content-bottom {
		border-color: var(--a-divide) !important;
	}
	/*标签栏*/
	.header-tabs-panel__item--active {
		color: var(--a-theme-color)!important;
		border-color: var(--a-theme-color)!important;
	}
	/*直播中*/
	.dynamic-live-item .living {
		background: var(--a-fill-2);
		color: var(--a-text-1) !important;
		border: none!important;
	}
	.bili-header .header-history-video__progress--inner {
		background: var(--a-theme-color);
	}
	/*历史 查看更多按钮*/
	.history-panel-popover .header-tabs-panel__content .view-all-history-btn {
		background: var(--a-fill-2);
		transition: .3s;
	}
	.history-panel-popover .header-tabs-panel__content .view-all-history-btn:hover {
		background: var(--a-fill-1)
	}
}

