        }

        .international-header .nav-search .bilibili-search-suggest:active,
        .international-header .nav-search .bilibili-search-history:active,
        .international-header .nav-search:focus-within .bilibili-search-suggest,
        .international-header .nav-search:focus-within .bilibili-search-history{
            display: block;
        }

.international-header .nav-search::after,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar::after{
            content: '';
            position: absolute;
            top: -60px;
            right: -10px;
            width: 100px;
            height: 100px;
            background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/searchBar_33.png);
            background-size: cover;
            z-index: -1;
            transition: .3s ease-out;
            opacity: 0;
        }

        .bili-header :not(.slide-down) .center-search-container .center-search__bar::after {
            background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/searchBar_33_2.png);
            background-size: cover;
        }

        .international-header .nav-search:focus-within::after,
        .bili-header :not(.slide-down) .center-search-container .center-search__bar:focus-within::after{
            transform: translatey(-20px);
            opacity: 1;
        } 

        .bili-header :not(.slide-down) #nav-searchform {
            overflow: unset!important;
        }

/*新版分区弹出层显示22娘*/
		@keyframes slideIn22 {
			from {
				transform: translateX(100%);
				opacity: 0;
			}
		}

		.n-drawer-mask::after {
			content: "";
			position: absolute;
			right: 200px;
			top: 30px;
			background: url(https://cdn.jsdelivr.net/gh/hakadao/bilibili-simple-home@master/img/drawer_mask_22.png) no-repeat;
			background-size: contain;
			width: 423px;
			height: 100%;
			pointer-events: none;
			animation: 1s slideIn22;
		}

/*移除排行榜*/
	.bili-grid {
		grid-template-columns: repeat(4, 1fr);
	}
	@media (max-width: 1099.9px) {
		.bili-grid {
			grid-template-columns: repeat(3, 1fr);
		}
	}
	.bili-grid aside {
		display: none;
	}

/*鼠标滑过视频预览放大效果*/
        [class*="-card"] [class$="-wrap"] {
            z-index: 0;
        }
        [class*="-card"] [class$="-wrap"]:hover {
            /*更改 z-index 令滑过视频放大了之后不受旁边视频遮挡*/
            z-index: 10;
        }
        [class$="-card"] [class*="-card__image"],
        [class$="video-card"] [class*="-card__image"]:hover [class*="-card__mask"] {
            transition: all .3s;
        }
        [class$="video-card"] [class*="-card__image"]:hover,
        [class$="video-card"] [class*="-card__image"]:hover [class*="-card__mask"] {
            transform: scale(1.1) translate3d(0, 0, 0);
            z-index: 999999;
            box-shadow: var(--a-depth-2);
        }
        [class$="video-card"] a + .bili-video-card__info {
            transition: .4s;
            position: relative;
            transform: translate3d(0, 0, 0);
        }
        [class$="video-card"] a + .bili-video-card__info::after {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: -1;
            background: transparent;
            border-radius: var(--a-radius);
            transition: .4s;
            pointer-events: none;
        }
        [class$="video-card"] a:hover + .bili-video-card__info::after {
            top: -2rem;
            left: -1rem;
            width: calc(100% + 2rem);
            height: calc(100% + 3rem);
        }
        [class$="video-card"] a:hover + .bili-video-card__info {
            transform: translate(0, 30%);
        }
        [class$="video-card"] a:hover + .bili-video-card__info::after {
            background: var(--a-elevated-1-solid);
            box-shadow: var(--a-depth-2);
        }

