.la-i > .la-m {
	background: bg !important;
}
.la-k .la-l, .la-k .la-r {
	border: 3px none bg !important;
}
.msg, .msgb {
	color: txt !important;
}
.submit_as_linkm, #loading a {
	color: lin !important;
}
}

