==/UserStyle== */
@-moz-document url-prefix("https://t.bilibili.com") {
	/*动态页背景*/
	html,
	html body,
	html:not(.iframe),
	html:not(.iframe) body {
		background: var(--activity-bgimg) no-repeat fixed 50% 0;
		background-size: cover;
		transform: none;
	}
	.bg {
		background: #fff0!important;
	}
}
@-moz-document url-prefix("https://t.bilibili.com") {
	/*动态页顶栏*/
	/*改自hakadao的https://userstyles.world/style/3022/bilibili*/
	@font-face {
		/*导入logo字体*/
		font-family: 'Bilibili Icon';
		src: url('https://patchwiki.biligame.com/images/gt/4/47/q4qr5ai4ckncd7890wil4kxuageskca.ttf') format('truetype');
		font-weight: normal;
		font-style: normal;
	}
	/*顶栏logo*/
	.bili-header .left-entry__title::before {
		content: "\0042";
		font-family: 'Bilibili Icon';
		font-style: normal;
		font-weight: normal;
		line-height: 0;
		font-size: 60px;
		color: white;
		margin-right: 12px;
	}
	/*LOGO修改*/
	.bili-header .left-entry__title .mini-header__title > span,
	.bili-header .left-entry__title .mini-header__logo {
		display: none;
	}
	.bili-header .left-entry__title .mini-header__title .mini-header__arrow {
		color: white;
		filter: drop-shadow(0 0 4px rgba(0, 0, 0, .3));
	}
	/*顶栏未读信息、未读动态*/
	.bili-header [class^="red-num--"],
	/*顶栏弹窗，未读信息*/
	.bili-header .message-entry-popover [class$="--num"] {
		background-color: var(--activity-theme-color)
	}
	/*搜索栏*/
	/*搜索栏输入框文字清除图标*/
	.bili-header .center-search-container .center-search__bar .nav-search-content .nav-search-clean svg * {
		fill: var(--activity-text-3)
	}
	.bili-header .center-search-container .center-search__bar .nav-search-content .nav-search-input:focus,
	.bili-header .center-search-container .center-search__bar .nav-search-content .nav-search-input:active,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-actived .nav-search-content,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-focus .nav-search-content {
		background-color: transparent!important;
	}
	/*修正当搜寻栏存在搜索建议、历史搜索但关闭是，仍然阻碍游标事件问题*/
	.center-search__bar {
		pointer-events: none;
	}
	.center-search__bar > * {
		pointer-events: auto;
	}
	.bili-header .center-search-container .center-search__bar.is-focus {
		box-shadow: none;
	}
	.bili-header .center-search-container .center-search__bar #nav-searchform:before {
		border-radius: 50px!important;
	}
	.bili-header .center-search-container .center-search__bar #nav-searchform:focus-within:before {
		border-radius: 10px!important;
	}
	#nav-searchform {
		background-color: var(--activity-content-1)!important;
		padding: 8px 48px 8px 6px!important;
		border: none!important;
		border-radius: 50px!important;
		box-shadow: var(--activity-depth-1);
		backdrop-filter: var(--activity-filter-glass);
		opacity: 1!important;
		transition: box-shadow .3s!important;
		/*防止搜索栏覆盖收藏弹窗*/
		z-index: 0!important;
	}
	#nav-searchform:focus-within {
		box-shadow: var(--activity-depth-2);
	}
	.slide-down #nav-searchform {
		box-shadow: var(--activity-depth-2);
	}
	.nav-search-input {
		color: var(--activity-text-1)!important;
	}
	.bili-header .slide-down .center-search-container .center-search__bar {
		width: 100%;
	}
	/*这里修改搜索栏的上间距 margin-top*/
	.bili-header :not(.slide-down) .center-search-container .center-search__bar {
		width: 500px;
		max-width: unset;
	}
	.bili-header .center-search-container .center-search__bar {
		transition: transform .3s;
	}
	.center-search-container .center-search__bar #nav-searchform.is-actived:before,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-focus:before,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-focus,
	.bili-header .slide-down .center-search-container .center-search__bar #nav-searchform,
	.bili-header .slide-down .center-search-container .center-search__bar #nav-searchform.is-focus,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-actived .nav-search-content,
	.bili-header .center-search-container .center-search__bar #nav-searchform.is-focus .nav-search-content,
	.bili-header .center-search-container .center-search__bar .nav-search-content .nav-search-input:focus,
	.bili-header .center-search-container .center-search__bar .nav-search-content .nav-search-input:active {
		background: transparent;
	}
	.bili-header .slide-down .center-search-container .center-search__bar #nav-searchform,
	.bili-header .slide-down .center-search-container .center-search__bar #nav-searchform:hover {
		background: var(--activity-elevated-2)!important;
		backdrop-filter: var(--activity-filter-glass);
	}
	.bili-header .slide-down .center-search-container .center-search__bar #nav-searchform:hover,
	.bili-header .center-search-container .center-search__bar #nav-searchform {
		border: none;
	}
	.bili-header :not(.slide-down) .center-search-container .center-search__bar #nav-searchform {
		opacity: 1;
		background-color: var(--activity-elevated-1);
		backdrop-filter: var(--activity-filter-glass);
		color: var(--activity-text-1);
	}
	/*搜索栏搜索图标按钮*/
	.bili-header .center-search-container .center-search__bar .nav-search-btn {
		border-radius: 40px!important;
		top: unset;
		transition: .3s;
	}
	.bili-header .center-search-container .center-search__bar .nav-search-btn:hover {
		background: var(--activity-fill-1);
		transform: rotate(-10deg) scale(0.9);
	}
	/*搜索结果面板*/
	.bili-header .search-panel {
		padding: unset!important;
		border: none;
		background: var(--activity-content-1);
		backdrop-filter: var(--activity-filter-glass);
		box-shadow: var(--activity-depth-2);
		border-radius: calc(var(--activity-radius) * 2 - 2px)!important;
		margin: .5rem 0;
		display: block!important;
		transition: .3s ease-out;
		transform: translateY(10px) scale(.95);
		opacity: 0;
		pointer-events: none;
	}
	#nav-searchform.is-focus + .search-panel {
		transform: translateY(0%) scale(1);
		opacity: 1;
		pointer-events: auto;
	}
	.bili-header .slide-down .search-panel {
		background: var(--activity-elevated-2);
	}
	.bili-header .search-panel .history {
		margin-top: 14px;
	}
	.bili-header .suggestions {
		margin: unset;
		padding: .5rem;
	}
	.bili-header .suggest-item {
		color: var(--activity-text-1);
		max-height: unset!important;
		padding: 0 .5rem;
		border-radius: 14px;
		transition: .3s;
	}
	.bili-header .suggest-item:last-child {
		margin-bottom: unset;
	}
	/*搜索历史*/
	.bili-header .histories .history-item {
		background: var(--activity-fill-1);
		transition: .3s;
		border-radius: var(--activity-radius);
	}
	.bili-header .histories .history-item:hover {
		background: var(--activity-theme-color-20);
		color: var(--activity-theme-color);
	}
	.bili-header .histories .history-item .close {
		display: block!important;
	}
	.bili-header .histories .history-item:hover .close svg,
	.bili-header .histories .history-item:focus .close svg {
		transform: scale(1);
	}
	.bili-header .histories .history-item .close svg {
		transition: .3s;
		transform: scale(0);
	}
	/*选中的搜索结果*/
	.bili-header .suggest-item.active,
	.bili-header .suggest-item:hover,
	.bili-header .suggest-item:focus {
		background: var(--activity-fill-1);
	}
	.bili-header .suggest_high_light {
		color: var(--activity-text-1);
		font-weight: 900;
		transition: .3s;
	}
	/*右侧搜索按钮*/
	.bili-header .center-search-container .center-search__bar .nav-search-btn {
		background-color: transparent;
		color: var(--activity-text-1);
		border-radius: 10px;
	}
	.bili-header .center-search-container .center-search__bar .nav-search-btn-text {
		display: none;
	}
	.bili-header .center-search-container .center-search__bar .nav-search-input::placeholder {
		opacity: 0;
	}
	.bili-header .search-panel {
		padding-bottom: 0;
	}
	/*分区栏*/
	.bili-header .bili-header__channel {
		background: var(--activity-bgcolor)!important;
	}
	.bili-header .channel-icons .icon-title {
		color: var(--activity-text-1);
	}
	.bili-header .bili-header__channel .channel-entry-more__link,
	.bili-header .bili-header__channel .channel-link {
		background-color: var(--activity-elevated-1-solid)!important;
		color: var(--activity-text-1)!important;
		border: none;
		border-radius: var(--activity-radius);
		box-shadow: var(--activity-depth-1);
		position: relative;
		z-index: 1;
	}
	.bili-header__channel .channel-entry-more__link::after,
	.bili-header .bili-header__channel .channel-link::after {
		content: "";
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		z-index: -1;
		transition: .3s ease-out;
		border-radius: var(--activity-radius);
	}
	.bili-header .bili-header__channel .channel-entry-more__link:hover::after,
	.bili-header .bili-header__channel .channel-link:hover::after {
		background: var(--activity-fill-1);
	}
	.bili-header .bili-header__channel .channel-items__left {
		border: none;
	}
	/*新动态通知*/
	.bili-header .channel-icons .channel-notify {
		background: var(--activity-theme-color)
	}
	/*动态 热门 频道*/
	.header-channel-fixed-left {
		flex-direction: column;
		width: 100%;
		min-width: unset;
		align-items: center;
	}
	/*顶栏右侧内容 帐号 消息 动态 收藏...*/
	.bili-header .slide-down .right-entry {
		/*这里把它们做成胶囊效果*/
		backdrop-filter: var(--activity-filter-glass);
		border-radius: 100px;
		box-shadow: var(--activity-depth-2);
		background: var(--activity-elevated-2);
		padding: 6px 0;
	}
	.bili-header .slide-down .left-entry .mini-header__title,
	.bili-header .slide-down .left-entry .entry-title,
	.bili-header .slide-down .left-entry .default-entry,
	.bili-header .slide-down .left-entry .loc-mc-box__text,
	.bili-header .slide-down .left-entry .download-entry,
	.bili-header .slide-down .left-entry .loc-entry {
		color: var(--activity-text-1);
	}
	/*顶栏登录*/
	.bili-header .header-login-entry {
		color: var(--activity-theme-color);
		background-color: var(--activity-content-1-solid);
	}
	/*顶栏右侧图标项目*/
	.bili-header .right-entry__outside {
		height: 36px;
		display: flex;
		justify-content: center;
	}
	/*大会员过期了那还是把大会员图标移除吧*/
	.bili-header .vip-wrap {
		display: none;
	}
	/*投稿图标不变色*/
	.bili-header .slide-down [class$="entry"] span:not(nth-last-child),
	.bili-header .slide-down [class$="entry"] svg {
		color: var(--activity-text-1)!important;
	}
	.bili-header .slide-down .right-entry .right-entry__outside .right-entry-icon {
		color: var(--activity-text-1);
	}
	.bili-header .right-entry {
		display: flex;
		justify-content: flex-end;
		padding: 8px 0;
		padding-left: 4px!important;
	}
	.bili-header :not(.slide-down) .right-entry {
		padding: 8px 0;
	}
	/*去除顶栏头像边框*/
	.bili-header .header-entry-mini .v-img > img,
	.bili-header .header-avatar-wrap .header-avatar-wrap--container .bili-avatar {
		border: none;
	}
	/*顶栏右侧面板*/
	.bili-header .bili-header__bar .right-entry__outside .right-entry-text {
		/*这里做的是鼠标滑过图标显示文字动画*/
		position: absolute;
		opacity: 0;
		transition: .3s ease-out;
		transform: translateY(100%);
	}
	.bili-header .bili-header__bar.slide-down .right-entry__outside .right-entry-text {
		/*当顶部链接效果是效果二时，右侧面板的图标和文字都有 filter: drop-shadow 的效果。slide-down 时要去掉阴影效果*/
		filter: unset;
	}
	.bili-header .right-entry__outside:hover .right-entry-text {
		opacity: 1;
		transform: translateY(0);
	}
	/*顶栏右侧面板的图标*/
	.bili-header .right-entry__outside svg {
		transition: .3s ease-out;
		animation: none!important;
	}
	.bili-header .slide-down .right-entry__outside svg {
		filter: unset;
	}
	/*滑过图标显示触发图标上移显示文字动画*/
	.bili-header .right-entry__outside:hover svg {
		transform: translateY(-100%);
		opacity: 0;
	}
	/*投稿禁用上述动画*/
	.bili-header .right-entry__outside:hover .header-upload-entry svg {
		transform: unset;
		opacity: unset;
	}
	/*小于 1279.9px 时禁用滑过图标上移动画，这个像素控制原本是不显示图标下方文字*/
	@media (max-width: 1279.9px) {
		.bili-header .right-entry__outside:hover svg {
			transform: unset;
			opacity: unset;
		}
	}
	/*投稿图标*/
	.bili-header .header-upload-entry {
		background: var(--activity-theme-color);
		color: white;
		border-radius: 50px;
		transition: .3s ease-out;
		margin-right: 12px;
	}
	.bili-header .header-upload-entry:hover {
		background: var(--activity-theme-color);
		filter: brightness(1.1)
	}
	/*顶栏遮罩*/
	.bili-header .bili-header__banner::before {
		content: "";
		position: absolute;
		width: 100%;
		height: 100%;
		background: var(--activity-mask-banner-color);
	}
	/*顶栏过渡效果*/
	.bili-header .bili-header__banner .taper-line {
		background: transparent;
	}
	/*banner 头图背景*/#i_cecream,
	.large-header {
		background: unset;
	}
	/*头图*/
	.bili-header .bili-header__banner {
		height: 100vh;
		max-height: unset;
		background: var(--activity-bgimg) center/cover!important;
		transition: background 1s;
	}
	/*去除 banner 跳转连结*/
	.bili-header .bili-header__banner .header-banner__inner {
		display: none;
	}
	.bili-header .bili-header__banner .head-title {
		display: none;
	}
	.bili-header .bili-header__bar.slide-down + .bili-header__banner::after {
		background-color: var(--activity-bgcolor);
		filter: opacity(.6);
	}
	/*去除礙眼物*/
	/*顶栏左侧多余项目，保留首页按钮*/
	.bili-header .left-entry li:not(:nth-child(1)),
	/*大会员*/
	.bili-header .right-entry li:nth-child(2),
	/*热门搜寻*/
	.search-panel .trending,
	/*体验新版那个烦人的提示图*/
	.bili-dyn-version-control__reminding,
	/*广告*/
	.bili-footer {
		display: none!important;
	}
	/*顶栏*/
	.bili-header {
		color: var(--activity-text-1);
		transition: .3s;
	}
	/*顶栏条*/
	.bili-header .bili-header__bar {
		margin-top: 8px;
		/*修正banner点击顶栏消失问题*/
		display: flex!important;
	}
	/*顶栏右侧图标项目*/
	.bili-header .right-entry__outside {
		height: 36px;
		display: flex;
		justify-content: center;
	}
	.mini-header {
		background: #0000;
		box-shadow: 0 2px 4px #0000;
	}
}

