@-moz-document url-prefix("https://t.bilibili.com") {
	/*样式颜色、阴影、滤镜等变量，和自定义选项作出的修改*/
	/*改自hakadao的https://userstyles.world/style/3022/bilibili*/:root {
		--activity-bgcolor: hsl(220, 14%, 96%);
		--activity-theme-default: rgb(0, 179, 255);

		--activity-mask-banner-opacity: 0;
		--activity-mask-banner-color: hsla(219, 26%, 96%, var(--activity-mask-banner-opacity));

		--activity-content-1: rgba(255, 255, 255, 1);
		--activity-content-1-solid: rgba(255, 255, 255, 1);
		--activity-elevated-1: rgb(255, 255, 255);
		--activity-elevated-2: rgb(255, 255, 255);
		--activity-elevated-1-solid: rgb(255, 255, 255);
		--activity-elevated-2-solid: rgb(255, 255, 255);

		--activity-divide: rgba(60, 60, 67, 0.1);
		--activity-fill-1: rgba(120, 120, 128, 0.15);
		--activity-fill-2: rgba(120, 120, 128, 0.09);

		--activity-text-1: #000;
		--activity-text-2: hsla(240, 6%, 15%, .8);
		--activity-text-3: hsla(240, 6%, 25%, .6);

		--activity-radius: 10px;

		/*主题颜色*/
		--activity-theme-color: /*[[T_THEME_COLOR_NEW]]*/;
		--activity-theme-color-85: /*[[T_THEME_COLOR_NEW]]*/d6;
		--activity-theme-color-50: /*[[T_THEME_COLOR_NEW]]*/80;
		--activity-theme-color-40: /*[[T_THEME_COLOR_NEW]]*/66;
		--activity-theme-color-30: /*[[T_THEME_COLOR_NEW]]*/4d;
		--activity-theme-color-20: /*[[T_THEME_COLOR_NEW]]*/33;

		--activity-depth-1: 0 6px 8px rgba(0, 0, 0, 0.04), 0 2px 4px rgba(0, 0, 0, 0.03);
		--activity-depth-2: 0 9px 18px rgba(0, 0, 0, 0.07), 0 6px 12px rgba(0, 0, 0, 0.05);

		/*覆盖b站颜色变量*/
		--text1: var(--activity-text-1);
		--text2: var(--activity-text-2);
		--text3: var(--activity-text-3);
		--graph_bg_thick: var(--activity-fill-1);
		--graph_bg_regular: var(--activity-fill-2);
		--bg1: var(--activity-content-1-solid);
		--brand_blue: var(--activity-theme-color);
	}
	@supports (backdrop-filter: blur(15px)) {
		:root {
			--activity-filter-glass: blur(15px);
			--activity-content-1: rgba(255, 255, 255, .6);
			--activity-elevated-1: rgba(255, 255, 255, .6);
			--activity-elevated-2: rgba(255, 255, 255, 70%);
		}
	}

	/* custom chrome, edge scrollbar */
	::-webkit-scrollbar {
		width: 8px;
		height: 8px;
	}
	::-webkit-scrollbar-corner,
	::-webkit-scrollbar-track {
		background: transparent;
	}
	html > ::-webkit-scrollbar-track {
		background: var(--activity-bgcolor);
	}
	::-webkit-scrollbar-thumb {
		background-color: hsl(0 0% 50% / .6);
		border-radius: 20px;
	}
	/* custom firefox scrollbar */
	html > * {
		scrollbar-color: hsl(0 0% 50% / .6) var(--activity-bgcolor);
		scrollbar-width: thin!important;
	}
	* {
		scrollbar-color: hsl(0 0% 50% / .6) transparent;
		scrollbar-width: thin!important;
	}

	/*[[LOGO_COLOR]]*/
	/*[[FW_BANNER]]*/
	/*[[FW_FRAME]]*/
	/*[[FW_ICON]]*/
	/*[[FW_SHARE]]*/
	/*[[CAPSULE_DESIGN]]*/
	/*[[GPUS]]*/
	/*[[L_BLUR]]*/

	/*处理顶栏链接内容，使其在自定义背景下banner上方文字链接有阴影使其能看清文字*/
	.bili-header :not(.slide-down) .right-entry {
		backdrop-filter: var(--activity-filter-glass);
		border-radius: 100px;
		box-shadow: var(--activity-depth-1);
	}

	:root {
		--activity-bgimg: /*[[T_DIV_BG]]*/;
		--activity-bg-custom: /*[[T_CUSTOM_BG]]*/;
	}
}

